mapper.select:
select * from( select rownum rn, apno, name, start_price, best_bid_price, bid_unit, start_date, end_date from auction_product where
rownum <= #{pageNum} * #{amount})
where rn > (#{pageNum} - 1) * #{amount}

mapper.update:
update auction_product set name=#{name},
bid_unit=#{bid_unit} where apno =
#{apno}
